import logo from './logo.svg';


import './App.css';
import {
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  RouterProvider
} from "react-router-dom"
import { useState , useEffect } from "react"
//Hola
//Componentes
import Informacion from './Componentes/Informacion';
import Listado from './Componentes/Listado';
import Encabezado from './Componentes/Encabezado';
import Agregar from './Componentes/Agregar';



function App() {



  const [Disco,setDisco] = useState([{
    titulo:'',
    artista:'',
    fecha:0
  }])

  const [DiscosCargados,setDiscoscargados] = useState(false)

  useEffect(()=>{
    const ObtenerDisco = () =>{
        fetch('http://localhost:5000/api')
        .then(res => res.json())
        .then(res=>{
          setDisco(res)
          setDiscoscargados(true)  
        })
    }
    ObtenerDisco()
    console.log(DiscosCargados)
  },[])


const router = createBrowserRouter(
  createRoutesFromElements(
    <Route path="/" element={<Encabezado/>}>
      <Route index element={<Listado Disco={Disco} setDisco={setDisco} />} />
      <Route path="informacion" element={<Informacion />} />
      <Route path="agregar" element={<Agregar Disco={Disco} setDisco={setDisco} />} />


    </Route>
  )
)



  return (
    <RouterProvider router={router}/>
  );
}

export default App;
