    console.log(DiscosCargados)
    const Cambio = e => {
        setDisco({
            ...Disco,
            [e.target.name] : e.target.value
            
        })
        console.log(e.target.value)
    }

    let {titulo , artista , fecha} = Disco

console.log(Disco)

    return ( 
        <div className="componente">
            <h1>Agregar un disco</h1>
        {/*     {DiscosCargados && <p>{Discos}</p>} */}
           { <form>

                <div>
                    <label>Título del disco: </label>
                    <input value={titulo} name="" onChange={Cambio} type="text"></input>
                </div>
                <div>
                    <label>Artista: </label>
                    <input value={artista} name="" onChange={Cambio} type="text"></input>
                </div>
                <div>
                    <label>Año de publicación: </label>
                    <input value={fecha} name="" onChange={Cambio} type="text"></input>
                </div>
            </form> }
        </div>
     );
}
 
export default Agregar;
